import React, { useState } from "react";
import { Box, Typography, Grid, Collapse } from "@mui/material";

function CreditCardGrid() {
  const [open, setOpen] = useState(false);

  // List of credit card categories
  const creditCardCategories = [
    "IndusInd Bank Credit Card",
    "Saraswat Bank Credit Card",
    "Best RuPay Credit Cards",
    "Best Airport Lounge Access Credit Cards",
    "Best International Travel Credit Cards",
    "Best Hotel Booking Credit Cards",
    "Best Movie Ticket Booking Credit Cards",
    "Lifetime Free Credit Cards",
    "Best Cashback Credit Cards",
    "Best Credit Cards in India",
    "Best Fuel Credit Cards",
    "Best Reward Credit Cards",
    "Best Shopping Credit Cards",
    "Best Student Credit Cards",
    "Best Travel Credit Cards",
    "Balance Transfer Credit Card",
  ];

  return (
    <Box
      sx={{
        width: "100%",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        marginTop: "40px",
      }}
    >
      {/* Clickable Typography for toggling the box */}
      <Box
        onClick={() => setOpen(!open)}
        sx={{
          width: "300px",
          padding: "15px",
          textAlign: "center",
          backgroundColor: "#3593B0",
          color: "white",
          fontSize: "1.5rem",
          fontWeight: "600",
          borderRadius: "10px",
          cursor: "pointer",
          transition: "background 0.3s ease",
          "&:hover": {
            backgroundColor: "#2a7d97",
          },
        }}
      >
        Credit Cards
      </Box>

      {/* Expandable Box with Grid Layout */}
      <Collapse in={open} timeout="300">
        <Box
          sx={{
            marginTop: "20px",
            padding: "20px",
            backgroundColor: "white",
            boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
            borderRadius: "10px",
            width: "90%",
            maxWidth: "800px",
          }}
        >
          <Grid container spacing={2}>
            {creditCardCategories.map((card, index) => (
              <Grid item xs={12} sm={6} key={index}>
                <Typography
                  sx={{
                    fontSize: "1rem",
                    fontWeight: "500",
                    color: "#333",
                    padding: "8px",
                    borderRadius: "5px",
                    cursor: "pointer",
                    "&:hover": {
                      backgroundColor: "#f0f0f0",
                    },
                  }}
                >
                  {card}
                </Typography>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Collapse>
    </Box>
  );
}

export default Footer;
