import {
  AppBar,
  Box,
  Menu,
  MenuItem,
  Toolbar,
  Typography,
} from "@mui/material";
import { useState } from "react";
import KeyboardArrowDownSharpIcon from "@mui/icons-material/KeyboardArrowDownSharp";

function Navbar() {
  const creditCardsData = {
    "HDFC Bank Credit Card": ["Swiggy Credit Card", "Tata Neu Credit Card"],
    "SBI Credit Card": ["SBI Prime Credit Card", "SimplyCLICK SBI Card"],
  };

  const loanData = {
    "Personal Loan": ["KreditBee Personal Loan", "Fibe Personal Loan"],
    "Business Loan": ["KreditBee Business Loan", "Moneyview Business Loan"],
  };

  const insuranceData = ["Health Insurance", "Life Insurance"];

  const emiCalculatorData = [
    "Home Loan EMI Calculator",
    "Personal Loan EMI Calculator",
  ];

  const creditScoreData = {
    "Check CIBIL Score": ["Check CIBIL Score by PAN Card", "Check Free CIBIL Score"],
    "CIBIL Score for Banks": ["SBI CIBIL Score", "HDFC CIBIL Score"],
  };

  const [anchorEl, setAnchorEl] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState(null);

  const handleMenuOpen = (event, category) => {
    setAnchorEl(event.currentTarget);
    setSelectedCategory(category);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
    setSelectedCategory(null);
  };

  return (
    <AppBar
      sx={{
        backgroundColor: "white",
        boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
        position: "fixed",
        zIndex: 1100, // Ensure navbar stays on top
      }}
    >
      <Toolbar sx={{ display: "flex", justifyContent: "space-between" }}>
        {/* LOGO */}
        <img
          src="https://cdn2.mymoneymantra.com/white_logo_d23b343666.webp"
          alt="logo"
          style={{
            width: "150px", // Set appropriate width
            height: "50px",
            filter: "invert(41%) sepia(95%) saturate(5935%) hue-rotate(162deg)",
          }}
        />

        {/* NAVIGATION MENU */}
        <Box sx={{ display: "flex", gap: 5 }}>
          {[
            { name: "Credit Cards", data: creditCardsData },
            { name: "Loans", data: loanData },
            { name: "Insurance", data: insuranceData },
            { name: "EMI Calculator", data: emiCalculatorData },
            { name: "Credit Score", data: creditScoreData },
          ].map(({ name, data }) => (
            <Box
              key={name}
              onMouseEnter={(event) => handleMenuOpen(event, name)}
              onMouseLeave={handleMenuClose}
              sx={{
                cursor: "pointer",
                display: "flex",
                alignItems: "center",
                position: "relative",
              }}
            >
              {/* NAV ITEM */}
              <Typography
                sx={{
                  color: "#0097a7",
                  fontWeight: 700,
                  fontSize: "16px",
                  transition: "color 0.3s",
                }}
              >
                {name}
              </Typography>
              <KeyboardArrowDownSharpIcon sx={{ fontSize: "20px", color: "#0097a7" }} />

              {/* DROPDOWN MENU */}
              <Menu
                anchorEl={anchorEl}
                open={Boolean(anchorEl) && selectedCategory === name}
                onClose={handleMenuClose}
                MenuListProps={{ onMouseLeave: handleMenuClose }}
                sx={{
                  mt: 1,
                  "& .MuiPaper-root": { width: "250px" },
                }}
              >
                {Array.isArray(data)
                  ? data.map((item, index) => (
                      <MenuItem
                        key={index}
                        onClick={handleMenuClose}
                        sx={{ color: "#0097a7", fontWeight: 300, fontSize: "14px" }}
                      >
                        {item}
                      </MenuItem>
                    ))
                  : Object.entries(data).map(([category, subItems], index) => (
                      <Box key={index}>
                        <Typography sx={{ fontWeight: "bold", px: 2, py: 1 }}>{category}</Typography>
                        {subItems.map((item, subIndex) => (
                          <MenuItem
                            key={subIndex}
                            onClick={handleMenuClose}
                            sx={{ color: "#0097a7", fontWeight: 300, fontSize: "14px" }}
                          >
                            {item}
                          </MenuItem>
                        ))}
                      </Box>
                    ))}
              </Menu>
            </Box>
          ))}

          {/* BLOG LINK */}
          <Typography
            sx={{
              color: "#0097a7",
              fontWeight: 700,
              fontSize: "16px",
              cursor: "pointer",
              transition: "color 0.3s",
            }}
          >
            Blogs
          </Typography>
        </Box>
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
