import React from "react";
import { Box, Typography, Button } from "@mui/material";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Autoplay } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";

const banners = [
  {
    title: "Unlock Your Dreams with ",
    highlight: "Personal Loan",
    description:
      "Flexible terms and competitive rates to help you fund your next big purchase or dream vacation.",
    image: "/images/slider1.jpg",
    link: "/personal-loans",
    buttonText: "Compare & Apply",
  },
  {
    title: "Instant Loan up to ₹5 lakhs in 5 minutes from ",
    highlight: "Poonawalla Fincorp",
    description: "",
    image: "/images/slider2.jpg",
    link: "https://instant-pocket-loan.poonawallafincorp.com",
    buttonText: "Apply Now",
  },
  {
    title: "Your New ",
    highlight: "Home Journey ",
    titlebottom: "Begins Here",
    description:
      "You simply find Dream Home, we will work towards making it reality. Get assistance even after disbursement.",
    image: "/images/slider3.jpg",
    link: "/home-loans",
    buttonText: "Compare & Apply",
  },
  {
    titletop: "Credit Card",
    title: " with Endless Possibilities",
    highlight: "",
    description:
      "Compare the top cards in categories like shopping, travel, luxury and Apply for the best suitable Credit Card.",
    image: "/images/slider4.jpg",
    link: "/credit-cards",
    buttonText: "Explore Cards",
  },
];

const Header = () => {
  return (
    <Box sx={{ position: "relative", width: "100%", height: 600 }}>
      <Swiper
        modules={[Navigation, Pagination, Autoplay]}
        navigation
        pagination={{ clickable: true }}
        autoplay={{ delay: 3000, disableOnInteraction: false }}
        loop
        style={{ width: "100%", height: "100%" }}
      >
        {banners.map((banner, index) => (
          <SwiperSlide key={index}>
            <Box
              sx={{
                width: "100%",
                height: "100%",
                backgroundImage: `url(${banner.image})`,
                backgroundSize: "cover",
                backgroundPosition: "center",
                backgroundRepeat: "no-repeat",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                padding: 3,
              }}
            >
              <Box
                sx={{
                  padding: 3,
                  borderRadius: 2,
                  maxWidth: 550,
                  textAlign: "left",
                  backgroundColor: "rgba(255, 255, 255, 0.7)",
                  backdropFilter: "blur(5px)",
                }}
              >
                <Typography
                  variant="h1"
                  sx={{
                    fontWeight: "900",
                    fontSize: "3.4rem",
                    lineHeight: "4rem",
                    marginBottom: "0.5rem",
                  }}
                >
                  <span style={{ color: "#4A9B85" }}>{banner.titletop}</span>
                  {banner.title}
                  <span style={{ color: "#4A9B85" }}>{banner.highlight} </span>
                  <span style={{ color: "black" }}>{banner.titlebottom}</span>
                </Typography>
                <img
                  src="/images/sliderline.jpg"
                  alt="line"
                  style={{ width: "70%" }}
                />
                {banner.description && (
                  <Typography
                    variant="body1"
                    sx={{
                      mt: 2,
                      color: "black",
                      fontSize: "1.4rem",
                      lineHeight: "2rem",
                      fontWeight: "bolder",
                    }}
                  >
                    {banner.description}
                  </Typography>
                )}
                <Button
                  href={banner.link}
                  variant="contained"
                  sx={{
                    mt: 3,
                    backgroundImage:
                      "linear-gradient(45deg, #3593B0 0%, #4A9B85 100%)",
                    color: "white",
                    width: "230px",
                    textAlign: "center",
                    fontSize: "1.125rem",
                    lineHeight: "1.75rem",
                    padding: "10px",
                    fontWeight: "700",
                  }}
                >
                  {banner.buttonText}
                </Button>
              </Box>
            </Box>
          </SwiperSlide>
        ))}
      </Swiper>
    </Box>
  );
};

export default Header;
