import React, { useState, useEffect } from "react";
import { Box, Typography, Button } from "@mui/material";

const banners = [
  {
    title: "Unlock Your Dreams with",
    highlight: "Personal Loan",
    description: "Flexible terms and competitive rates to help you fund your next big purchase or dream vacation.",
    image: "/images/slider1.jpg",
    link: "/personal-loans",
    buttonText: "Compare & Apply",
  },
  {
    title: "Instant Loan up to ₹5 lakhs in 5 minutes from",
    highlight: "Poonawalla Fincorp",
    image: "/images/slider2.jpg",
    link: "https://instant-pocket-loan.poonawallafincorp.com",
    buttonText: "Apply Now",
  },
  {
    title: "Your New",
    highlight: "Home Journey Begins Here",
    description: "Find your dream home, and we'll make it a reality. Get assistance even after disbursement.",
    image: "/images/slider3.jpg",
    link: "/home-loans",
    buttonText: "Compare & Apply",
  },
  {
    title: "Credit Card with Endless Possibilities",
    description: "Compare top cards for shopping, travel, and luxury. Apply for the best card now.",
    image: "/images/slider4.jpg",
    link: "/credit-cards",
    buttonText: "Explore Cards",
  },
];

const Header = () => {
    const [currentIndex, setCurrentIndex] = useState(0);
  
    useEffect(() => {
      const interval = setInterval(() => {
        setCurrentIndex((prev) => (prev + 1) % banners.length);
      }, 5000);
      return () => clearInterval(interval);
    }, []);
  
    return (
      <Box sx={{ width: "100%", height: "600px", position: "relative", overflow: "hidden" }}>
        {banners.map((banner, index) => (
          <Box
            key={index}
            sx={{
              position: "absolute",
              width: "100%",
              height: "100%",
              background: `url(${banner.image}) center/cover no-repeat`,
              opacity: index === currentIndex ? 1 : 0,
              transition: "opacity 0.8s ease-in-out",
            }}
          >
            {/* Text Directly on Image */}
            <Box sx={{ position: "absolute", top: "50%", left: "10%", transform: "translateY(-50%)", color: "#fff" }}>
              <Typography
                variant="h3"
                fontWeight={700}
                sx={{
                  textShadow: "2px 2px 8px rgba(0,0,0,0.6)", // Adds shadow to improve readability
                }}
              >
                {banner.title}{" "}
                <Typography component="span" sx={{ color: "#ffcc33", fontWeight: 800 }}>
                  {banner.highlight}
                </Typography>
              </Typography>
              {banner.description && (
                <Typography
                  variant="h6"
                  sx={{ mt: 2, textShadow: "1px 1px 6px rgba(0,0,0,0.6)" }}
                >
                  {banner.description}
                </Typography>
              )}
              <Button
                href={banner.link}
                sx={{
                  mt: 3,
                  background: "#2288a0",
                  color: "#fff",
                  px: 4,
                  py: 1,
                  borderRadius: 2,
                  fontWeight: 600,
                  "&:hover": { background: "#ffcc33", color: "#222" },
                }}
              >
                {banner.buttonText}
              </Button>
            </Box>
          </Box>
        ))}
  
        {/* Dots Navigation */}
        <Box sx={{ position: "absolute", bottom: 20, left: "50%", transform: "translateX(-50%)", display: "flex", gap: 1 }}>
          {banners.map((_, index) => (
            <Box
              key={index}
              onClick={() => setCurrentIndex(index)}
              sx={{
                width: 12,
                height: 12,
                borderRadius: "50%",
                background: index === currentIndex ? "#fff" : "#bbb",
                cursor: "pointer",
              }}
            />
          ))}
        </Box>
      </Box>
    );
  };
  
  export default Header;
