import React, { useState, useEffect } from "react";
import { Box, Typography, Button, IconButton, useTheme, useMediaQuery } from "@mui/material";
import { ArrowBackIos, ArrowForwardIos } from "@mui/icons-material";

const banners = [
  {
    title: "Unlock Your Dreams with",
    highlight: "Personal Loan",
    description: "Flexible terms and competitive rates to help you fund your next big purchase or dream vacation.",
    image: "/images/3.png",
    link: "/personal-loans",
    buttonText: "Compare & Apply",
  },
  {
    title: "Instant Loan up to ₹5 lakhs in 5 minutes from",
    highlight: "Poonawalla Fincorp",
    description: "",
    image: "/images/1.png",
    link: "https://instant-pocket-loan.poonawallafincorp.com",
    buttonText: "Apply Now",
  },
  {
    title: "Your New",
    highlight: "Home Journey Begins Here",
    description: "You simply find Dream Home, we will work towards making it reality. Get assistance even after disbursement.",
    image: "/images/4.png",
    link: "/home-loans",
    buttonText: "Compare & Apply",
  },
  {
    title: "Credit Card with Endless Possibilities",
    highlight: "",
    description: "Compare the top cards in categories like shopping, travel, luxury and Apply for the best suitable Credit Card.",
    image: "/images/2.png",
    link: "/credit-cards",
    buttonText: "Explore Cards",
  },
];

const Header = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % banners.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <Box sx={{ width: "100%", position: "relative", overflow: "hidden" }}>
      {/* Banner Wrapper */}
      <Box sx={{ width: "100%", height: isMobile ? "350px" : "650px", position: "relative" }}>
        {banners.map((banner, index) => (
          <Box
            key={index}
            sx={{
              position: "absolute",
              width: "100%",
              height: "100%",
              backgroundImage: `url(${banner.image})`,
              backgroundSize: "cover",
              backgroundPosition: "center",
              backgroundRepeat: "no-repeat",
              transition: "opacity 0.8s ease-in-out",
              opacity: index === currentIndex ? 1 : 0,
            }}
          >
            {/* Overlay */}
            <Box sx={{ width: "100%", height: "100%", backgroundColor: "rgba(0, 0, 0, 0.4)" }} />

            {/* Content */}
            <Box
              sx={{
                position: "absolute",
                top: 0,
                left: 0,
                width: "100%",
                height: "100%",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                textAlign: "left",
                px: 3,
              }}
            >
              <Box sx={{ maxWidth: "600px", color: "#fff" }}>
                <Typography variant={isMobile ? "h4" : "h3"} fontWeight={800} sx={{ textShadow: "0px 3px 6px rgba(0,0,0,0.3)" }}>
                  {banner.title}{" "}
                  <Typography component="span" sx={{ color: "#ffcc33", fontWeight: 800 }}>
                    {banner.highlight}
                  </Typography>
                </Typography>
                {banner.description && (
                  <Typography variant="h6" sx={{ mt: 2, textShadow: "0px 2px 4px rgba(0,0,0,0.3)" }}>
                    {banner.description}
                  </Typography>
                )}
                <Button
                  href={banner.link}
                  sx={{
                    mt: 3,
                    px: 4,
                    py: 1.5,
                    background: "linear-gradient(45deg, #2288a0 0%, #4A9B85 100%)",
                    color: "#fff",
                    fontSize: "18px",
                    fontWeight: 700,
                    borderRadius: "8px",
                    textTransform: "none",
                    boxShadow: "0 3px 12px rgba(0,0,0,0.3)",
                    "&:hover": {
                      background: "#ffcc33",
                      color: "#222",
                      transform: "translateY(-3px)",
                      boxShadow: "0 5px 15px rgba(0,0,0,0.4)",
                    },
                  }}
                >
                  {banner.buttonText}
                </Button>
              </Box>
            </Box>
          </Box>
        ))}
      </Box>

      {/* Navigation Arrows */}
      <IconButton
        onClick={() => setCurrentIndex((prevIndex) => (prevIndex === 0 ? banners.length - 1 : prevIndex - 1))}
        sx={{
          position: "absolute",
          top: "50%",
          left: "20px",
          transform: "translateY(-50%)",
          backgroundColor: "rgba(255,255,255,0.9)",
          color: "#333",
          fontSize: "22px",
          zIndex: 10,
          "&:hover": { backgroundColor: "#fff", transform: "scale(1.1)" },
        }}
      >
        <ArrowBackIos />
      </IconButton>

      <IconButton
        onClick={() => setCurrentIndex((prevIndex) => (prevIndex + 1) % banners.length)}
        sx={{
          position: "absolute",
          top: "50%",
          right: "20px",
          transform: "translateY(-50%)",
          backgroundColor: "rgba(255,255,255,0.9)",
          color: "#333",
          fontSize: "22px",
          zIndex: 10,
          "&:hover": { backgroundColor: "#fff", transform: "scale(1.1)" },
        }}
      >
        <ArrowForwardIos />
      </IconButton>

      {/* Dots Navigation */}
      <Box sx={{ position: "absolute", bottom: "25px", left: 0, right: 0, display: "flex", justifyContent: "center", gap: "12px" }}>
        {banners.map((_, index) => (
          <Box
            key={index}
            onClick={() => setCurrentIndex(index)}
            sx={{
              width: "14px",
              height: "14px",
              borderRadius: "50%",
              background: index === currentIndex ? "#01151b" : "linear-gradient(45deg, #2288a0 0%, #4A9B85 100%)",
              transition: "all 0.3s ease",
              cursor: "pointer",
              transform: index === currentIndex ? "scale(1.3)" : "scale(1)",
            }}
          />
        ))}
      </Box>
    </Box>
  );
};

export default Header;
