import React, { useState, useEffect } from "react";
import { AppBar, Toolbar } from "@mui/material";

function Navbar() {
  const [scrolling, setScrolling] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolling(true);
      } else {
        setScrolling(false);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <AppBar
      sx={{
        backgroundColor: "white",
        boxShadow: scrolling ? "0px 4px 10px rgba(0, 0, 0, 0.1)" : "none",
        position: "fixed",
        transition: "box-shadow 0.3s ease-in-out",
      }}
    >
      <Toolbar>
        <img
          src="https://cdn2.mymoneymantra.com/white_logo_d23b343666.webp?format=avif&quality=80&width=195&height=45"
          alt="logo"
          style={{
            filter: "invert(39%) sepia(41%) saturate(447%) hue-rotate(120deg)", // Sea green effect
          }}
        />
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
