import { AppBar, Box, Menu, MenuItem, Toolbar, Typography } from "@mui/material";
import { useState } from "react";
import ArrowDropDownIcon from "@mui/icons-material/ArrowDropDown";
import ArrowRightIcon from "@mui/icons-material/ArrowRight";

const creditCardsData = {
  "HDFC Bank Credit Card": [
    "Swiggy Credit Card",
    "Tata Neu Credit Card",
    "HDFC Millennia Credit Card",
    "HDFC Freedom Credit Card",
    "HDFC IndianOil Credit Card",
    "HDFC MoneyBack Credit Card",
    "HDFC UPI RuPay Credit Card",
    "HDFC MoneyBack+ Credit Card",
  ],
  "SBI Credit Card": [
    "SBI Prime Credit Card",
    "SimplyCLICK SBI Card",
    "SBI Elite Credit Card",
    "SimplySave SBI Card",
    "SBI PULSE Credit Card",
    "BPCL SBI Credit Card",
    "IRCTC RuPay Credit Card",
  ],
  "Popular Credit Card": [
    "Pop Club Credit Card",
    "Anq Phi Credit Card",
    "Jupiter Credit Card",
    "OneCard Credit Card",
    "ZET Magnet FD Credit Card",
    "Rupicard Credit Card",
    "Kiwi Credit Card",
  ],
  "IDFC Bank Credit Card": [
    "IDFC Wealth Credit Card",
    "IDFC Millennia Credit Card",
    "IDFC Classic Credit Card",
    "IDFC WOW Credit Card",
    "IDFC Select Credit Card",
  ],
  "Axis Bank Credit Card": [
    "Airtel Axis Bank Credit Card",
    "Axis Bank LIC Signature Credit Card",
    "Axis Bank SELECT Credit Card",
    "Axis Bank Rewards Credit Card",
    "Axis Bank Neo Credit Card",
    "Axis Bank MY ZONE Credit Card",
    "Axis Bank Magnus Credit Card",
    "LIC Axis Platinum Credit Card",
    "Axis Bank ACE Credit Card",
    "IndianOil Axis Bank Credit Card",
  ],
  "HSBC Bank Credit card": [
    "HSBC Visa Platinum Credit Card",
    "HSBC Live+ Credit Card",
    "HSBC Premier Credit Card",
    "HSBC Bank Smart Value Credit Card",
  ],
  "AU Bank Credit Card": [
    "AU Bank LIT Credit Card",
    "AU Bank Zenith Credit Card",
    "AU Bank Vetta Credit Card",
    "AU Bank Altura Credit Cards",
    "AU Bank Altura Plus Credit Card",
  ],
  "American Express Credit Card": [
    "American Express Gold Credit Card",
    "American Express SmartEarn Credit Card",
    "American Express Payback Credit Card",
  ],
  "ICICI Bank Credit Card": [
    "ICICI Bank Rubyx Credit Card",
    "ICICI Bank Coral Credit Card",
    "ICICI Bank Platinum Chip Credit Card",
    "ICICI Bank Expressions Credit Card",
    "ICICI Bank Coral Rupay Credit Card",
    "Amazon Pay ICICI Credit Card",
    "ICICI Bank HPCL Super Saver Credit Card",
    "ICICI Bank Sapphiro Credit Card",
    "ICICI Bank Emeralde Credit Card",
    "ICICI Bank Emeralde Private Metal Credit Card",
  ],
  "IndusInd Bank Credit Cards": [
    "EazyDiner IndusInd Bank Platinum Credit Card",
    "IndusInd Bank Samman RuPay Credit Card",
    "IndusInd Bank Platinum RuPay Credit Card",
    "IndusInd Bank Nexxt Credit Card",
    "IndusInd Bank Platinum Visa Credit Card",
    "IndusInd Bank Aura Edge Credit Card",
    "IndusInd Bank Legend Credit Card",
  ],
};

function Navbar() {
  const [anchorEl, setAnchorEl] = useState(null);
  const [subMenuAnchorEl, setSubMenuAnchorEl] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState(null);

  // Open main dropdown
  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };
  
  // Close main dropdown
  const handleMenuClose = () => {
    setAnchorEl(null);
    setSubMenuAnchorEl(null);
  };

  // Open nested submenu
  const handleSubMenuOpen = (event, category) => {
    setSubMenuAnchorEl(event.currentTarget);
    setSelectedCategory(category);
  };

  // Close nested submenu
  const handleSubMenuClose = () => {
    setSubMenuAnchorEl(null);
  };

  return (
    <AppBar
      sx={{
        backgroundColor: "white",
        boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
        position: "fixed",
      }}
    >
      <Toolbar>
        <img
          src="https://cdn2.mymoneymantra.com/white_logo_d23b343666.webp?format=avif&quality=80&width=195&height=55"
          alt="logo"
          style={{
            filter: "invert(41%) sepia(95%) saturate(5935%) hue-rotate(162deg)",
            marginTop: "20px",
            marginBottom: "20px",
          }}
        />

        <Box sx={{ display: "flex", gap: 7, ml: 2 }}>
          {/* Credit Cards Dropdown */}
          <Box
            onMouseEnter={handleMenuOpen}
            onMouseLeave={handleMenuClose}
            sx={{ position: "relative", cursor: "pointer", display: "flex", alignItems: "center" }}
          >
            <Box sx={{ display: "flex", alignItems: "center", gap: 0.5 }}>
              <Typography
                sx={{
                  color: "#0097a7",
                  fontWeight: 700,
                  fontSize: "16px",
                }}
              >
                Credit Cards
              </Typography>
              <ArrowDropDownIcon sx={{ color: "#0097a7", fontSize: "20px" }} />
            </Box>

            {/* Main Dropdown */}
            <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleMenuClose} MenuListProps={{ onMouseLeave: handleMenuClose }}>
              {Object.keys(creditCardsData).map((category, index) => (
                <MenuItem
                  key={index}
                  onMouseEnter={(event) => handleSubMenuOpen(event, category)}
                  sx={{ color: "#0097a7", fontWeight: 700, fontSize: "16px", display: "flex", justifyContent: "space-between" }}
                >
                  {category}
                  <ArrowRightIcon sx={{ fontSize: "18px", color: "#0097a7" }} />

                  {/* Nested Submenu */}
                  {selectedCategory === category && (
                    <Menu anchorEl={subMenuAnchorEl} open={Boolean(subMenuAnchorEl)} onClose={handleSubMenuClose} sx={{ position: "absolute", left: "100%" }}>
                      {creditCardsData[category].map((card, i) => (
                        <MenuItem key={i} sx={{ color: "#0097a7", fontWeight: 700, fontSize: "14px" }}>
                          {card}
                        </MenuItem>
                      ))}
                    </Menu>
                  )}
                </MenuItem>
              ))}
            </Menu>
          </Box>

          {/* Other Navbar Items */}
          {["Loan", "Insurance", "EMI Calculator", "Credit Score", "Blogs"].map((item) => (
            <Typography key={item} sx={{ color: "#0097a7", fontWeight: 700, fontSize: "16px", cursor: "pointer" }}>
              {item}
            </Typography>
          ))}
        </Box>
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
