import { AppBar, Box, Menu, MenuItem, Toolbar, Typography } from "@mui/material";
import { useState } from "react";
import KeyboardArrowDownSharpIcon from "@mui/icons-material/KeyboardArrowDownSharp";
import ArrowRightIcon from "@mui/icons-material/ArrowRight";

const creditCardsData = {
  "HDFC Bank Credit Card": ["Swiggy Credit Card", "Tata Neu Credit Card", "HDFC Millennia Credit Card", "HDFC Freedom Credit Card"],
  "SBI Credit Card": ["SBI Prime Credit Card", "SimplyCLICK SBI Card", "SBI Elite Credit Card"],
  "Popular Credit Card": ["Pop Club Credit Card", "Anq Phi Credit Card", "Jupiter Credit Card"],
  "Axis Bank Credit Card": ["Axis Bank SELECT Credit Card", "Axis Bank Rewards Credit Card"],
};

function Navbar() {
  const [anchorEl, setAnchorEl] = useState(null); // Main menu anchor
  const [subMenuAnchorEl, setSubMenuAnchorEl] = useState(null); // Submenu anchor
  const [selectedCategory, setSelectedCategory] = useState(null);

  // Open Main Dropdown
  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  // Close Main Dropdown
  const handleMenuClose = () => {
    setAnchorEl(null);
    setSubMenuAnchorEl(null);
  };

  // Open Submenu when hovering over a category
  const handleSubMenuOpen = (event, category) => {
    setSelectedCategory(category);
    setSubMenuAnchorEl(event.currentTarget);
  };

  return (
    <AppBar
      sx={{
        backgroundColor: "white",
        boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
        position: "fixed",
      }}
    >
      <Toolbar>
        <img
          src="https://cdn2.mymoneymantra.com/white_logo_d23b343666.webp?format=avif&quality=80&width=195&height=55"
          alt="logo"
          style={{
            filter: "invert(41%) sepia(95%) saturate(5935%) hue-rotate(162deg)",
            marginTop: "20px",
            marginBottom: "20px",
          }}
        />

        <Box sx={{ display: "flex", gap: 7, ml: 2 }}>
          {/* Credit Cards Dropdown */}
          <Box
            onMouseEnter={handleMenuOpen}
            onMouseLeave={handleMenuClose}
            sx={{ position: "relative", cursor: "pointer", display: "flex", alignItems: "center" }}
          >
            <Box sx={{ display: "flex", alignItems: "center", gap: 0.5 }}>
              <Typography sx={{ color: "#0097a7", fontWeight: 700, fontSize: "16px" }}>
                Credit Cards
              </Typography>
              <KeyboardArrowDownSharpIcon sx={{ fontSize: "20px", color: "#0097a7" }} />
            </Box>

            {/* Main Dropdown */}
            <Menu
              anchorEl={anchorEl}
              open={Boolean(anchorEl)}
              onClose={handleMenuClose}
              MenuListProps={{
                onMouseLeave: handleMenuClose, // Close when leaving the whole menu
              }}
              sx={{ mt: 1 }}
            >
              {Object.keys(creditCardsData).map((category, index) => (
                <MenuItem
                  key={index}
                  onMouseEnter={(event) => handleSubMenuOpen(event, category)}
                  sx={{
                    color: "#0097a7",
                    fontWeight: 700,
                    fontSize: "16px",
                    display: "flex",
                    justifyContent: "space-between",
                    width: "250px",
                  }}
                >
                  {category}
                  <ArrowRightIcon sx={{ fontSize: "18px", color: "#0097a7" }} />

                  {/* Nested Submenu */}
                  {selectedCategory === category && (
                    <Menu
                      anchorEl={subMenuAnchorEl}
                      open={Boolean(subMenuAnchorEl)}
                      onClose={() => setSubMenuAnchorEl(null)}
                      anchorOrigin={{ vertical: "top", horizontal: "right" }}
                      transformOrigin={{ vertical: "top", horizontal: "left" }}
                      sx={{ pointerEvents: "auto", ml: 1 }}
                      MenuListProps={{
                        onMouseLeave: () => setSubMenuAnchorEl(null), // Close submenu on leave
                      }}
                    >
                      {creditCardsData[category].map((card, i) => (
                        <MenuItem key={i} sx={{ color: "#0097a7", fontWeight: 700, fontSize: "14px" }}>
                          {card}
                        </MenuItem>
                      ))}
                    </Menu>
                  )}
                </MenuItem>
              ))}
            </Menu>
          </Box>

          {/* Other Navbar Items */}
          {["Loan", "Insurance", "EMI Calculator", "Credit Score", "Blogs"].map((item) => (
            <Typography key={item} sx={{ color: "#0097a7", fontWeight: 700, fontSize: "16px", cursor: "pointer" }}>
              {item}
            </Typography>
          ))}
        </Box>
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
