import { AppBar, Box, Menu, MenuItem, Toolbar, Typography} from "@mui/material";
import { useState } from "react";
import KeyboardArrowDownSharpIcon from '@mui/icons-material/KeyboardArrowDownSharp';

function Navbar() {

  const [anchorEl, setAnchorEl] = useState(null)

  const creditCards = [
    "HDFC Bank Credit Card",
    "SBI Credit Card",
    "Popular Credit Card",
    "IDFC Bank Credit Card",
    "Axis Bank Credit Card",
    "HSBC Bank Credit Card",
    "AU Bank Credit Card",
    "American Express Credit Card",
    "ICICI Bank Credit Card",
    "IndusInd Bank Credit Cards",
  ]

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget)
  }
  const handleMenuClose = () => {
    setAnchorEl(null)
  }
  return (
    <AppBar
      sx={{
        backgroundColor: "white",
        boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
        position: "fixed",
      }}
    >
      <Toolbar>
        <img
          src="https://cdn2.mymoneymantra.com/white_logo_d23b343666.webp?format=avif&quality=80&width=195&height=55"
          alt="logo"
          style={{
            filter: "invert(41%) sepia(95%) saturate(5935%) hue-rotate(162deg)",
            marginTop: "20px",
            marginBottom: "20px",
          }}
        />

        <Box sx={{ display: "flex", gap: 7, ml:2}}>
        <Box
            onMouseEnter={handleMenuOpen}
            onMouseLeave={handleMenuClose}
            sx={{ position: "relative", cursor: "pointer", display: "flex", alignItems: "center" }}
          >
            <Typography
              sx={{
                color: "#0097a7",
                fontWeight: 700,
                fontSize: "16px",
              }}
            >
              Credit Cards <KeyboardArrowDownSharpIcon sx={{fontWeight:'700px'}} />
            </Typography>
            <Menu
              anchorEl={anchorEl}
              open={Boolean(anchorEl)}
              onClose={handleMenuClose}
              MenuListProps={{ onMouseLeave: handleMenuClose }}
              sx={{
                mt: 1,
                "& .MuiPaper-root": { width: "300px" },
              }}
            >
              {creditCards.map((card, index) => (
                <MenuItem key={index} onClick={handleMenuClose} sx={{ color: "#0097a7",
                  fontWeight: 300,
                  fontSize: "16px",}}>
                  {card}
                </MenuItem>
              ))}
            </Menu>
          </Box>
          {[
            "Loan",
            "Insurance",
            "EMI Calculator",
            "Credit Score",
            "Blogs",
          ].map((item) => (
            <Typography
              key={item}
              sx={{
                color: "#0097a7",
                fontWeight: 700,
                fontSize: "16px",
                cursor: "pointer",
                transition: "color 0.3s",
              }}
            >
              {item}
            </Typography>
          ))}
        </Box>
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
